import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Timer from "../components/Timer";
import QuestionCard from "../components/QuestionCard";
import Loader from "../components/Loader";
import { generateMCQs } from "../utils/openai";

export default function TestPage() {
  const navigate = useNavigate();
  const [config, setConfig] = useState(null);
  const [questions, setQuestions] = useState(null);
  const [loading, setLoading] = useState(true);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [answers, setAnswers] = useState({});
  const [paused, setPaused] = useState(false);
  const [timeExpired, setTimeExpired] = useState(false);

  // STEP 1: Load config from localStorage
  useEffect(() => {
    const cfg = localStorage.getItem("mockConfig");
    console.log("Retrieved mockConfig from localStorage:", cfg);
    if (!cfg) {
      alert("No test config found. Redirecting to home.");
      navigate("/");
      return;
    }
      console.log("üì• Raw response from API (before parse):", rawQuestions);
    try {
      const parsed = JSON.parse(cfg);
      console.log("Parsed config object:", parsed);
      setConfig(parsed);
    } catch (err) {
      console.error("Error parsing config:", err);
      navigate("/");
    }
  }, [navigate]);

  // STEP 2: Fetch questions from API
  useEffect(() => {
    if (!config) return;

    async function fetchQuestions() {
      setLoading(true);
      try {
        console.log("Sending request to generateMCQs with config:", config);
        const rawQuestions = await generateMCQs({
          topics: config.topics,
          questionCount: Number(config.questionCount),
          difficulty: config.difficulty,
          type: config.type,
        });
        //console.log("Raw response from generateMCQs:", rawQuestions);

        const parsed = JSON.parse(rawQuestions);
        console.log("Parsed questions array:", parsed);

        if (!Array.isArray(parsed) || parsed.length === 0) {
          throw new Error("Parsed questions are empty or invalid.");
        }

        setQuestions(parsed);
      } catch (err) {
        console.error("Failed to load or parse questions:", err);
        alert("Failed to load questions. Try again.");
        navigate("/");
      } finally {
        setLoading(false);
      }
    }

    fetchQuestions();
  }, [config, navigate]);

  // STEP 3: Answer logic
  function setAnswerForCurrent(ans) {
    setAnswers((prev) => ({
      ...prev,
      [currentIndex]: ans,
    }));
    console.log("Updated answers state:", {
      ...answers,
      [currentIndex]: ans,
    });
  }

  function nextQuestion() {
    if (currentIndex < questions.length - 1) setCurrentIndex(currentIndex + 1);
  }

  function prevQuestion() {
    if (currentIndex > 0) setCurrentIndex(currentIndex - 1);
  }

  function submitTest() {
    localStorage.setItem(
      "mockResult",
      JSON.stringify({ answers, questions, config })
    );
    console.log("Test submitted. Stored in localStorage:", {
      answers,
      questions,
      config,
    });
    navigate("/result");
  }

  if (loading) {
    console.log("Loading state is true. Showing loader...");
    return <Loader />;
  }

  if (timeExpired) {
    alert("Time is up! Test will be submitted automatically.");
    submitTest();
    return null;
  }

  if (!questions || questions.length === 0) {
    console.warn("Questions are empty. Cannot render test.");
    return (
      <div className="container py-4">
        <h4>‚ùå Failed to load questions. Please try again.</h4>
        <button className="btn btn-primary mt-3" onClick={() => navigate("/")}>
          üîÑ Go Back to Home
        </button>
      </div>
    );
  }

  // MAIN RENDER
  if (!loading && questions) {
  console.log("üì¶ Question passed to QuestionCard:", questions[currentIndex]);
  console.log("üß† User answer so far:", answers[currentIndex] || []);
}
  return (
    <div className="container py-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <Timer
          durationMinutes={config.duration}
          paused={paused}
          onExpire={() => setTimeExpired(true)}
        />
        <div>
          <button
            className="btn btn-warning me-2"
            onClick={() => setPaused((p) => !p)}
          >
            {paused ? "Resume" : "Pause"}
          </button>
          <button className="btn btn-danger" onClick={submitTest}>
            Submit Test
          </button>
        </div>
      </div>

      <QuestionCard
        question={questions[currentIndex]}
        index={currentIndex}
        total={questions.length}
        userAnswer={answers[currentIndex] || []}
        setUserAnswer={setAnswerForCurrent}
        type={config.type}
      />

      <div className="d-flex justify-content-between mt-3">
        <button
          className="btn btn-secondary"
          onClick={prevQuestion}
          disabled={currentIndex === 0}
        >
          Previous
        </button>
        <button
          className="btn btn-secondary"
          onClick={nextQuestion}
          disabled={currentIndex === questions.length - 1}
        >
          Next
        </button>
      </div>
    </div>
  );
}
