import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Timer from "../components/Timer";
import QuestionCard from "../components/QuestionCard";
import Loader from "../components/Loader";
import { generateMCQs } from "../utils/openai";

export default function TestPage() {
  const navigate = useNavigate();
  const [config, setConfig] = useState(null);
  const [questions, setQuestions] = useState(null);
  const [loading, setLoading] = useState(true);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [answers, setAnswers] = useState({});
  const [paused, setPaused] = useState(false);
  const [timeExpired, setTimeExpired] = useState(false);

  // STEP 1: Load config from localStorage
  useEffect(() => {
  if (!config) return;

  async function fetchQuestions() {
    setLoading(true);
    try {
      console.log("üöÄ Sending request to generateMCQs with config:", config);

      const rawQuestions = await generateMCQs({
        topics: config.topics,
        questionCount: Number(config.questionCount),
        difficulty: config.difficulty,
        type: config.type,
      });

      console.log("üì® Raw response from generateMCQs:", rawQuestions);

      let parsed;
      try {
        parsed = JSON.parse(rawQuestions);
      } catch (parseErr) {
        console.error("‚ùå JSON parsing failed:", parseErr);
        throw new Error("API returned invalid JSON");
      }

      console.log("üì¶ Parsed questions array:", parsed);

      if (!Array.isArray(parsed) || parsed.length === 0) {
        throw new Error("Parsed questions are empty or invalid.");
      }

      setQuestions(parsed);
    } catch (err) {
      console.error("üö´ Failed to fetch or parse questions:", err.message);
      alert("Failed to load questions. Try again.");
      navigate("/");
    } finally {
      setLoading(false);
    }
  }

  fetchQuestions();
}, [config, navigate]);


  // STEP 2: Fetch questions from API
  useEffect(() => {
    if (!config) return;

    async function fetchQuestions() {
      setLoading(true);
      try {
        console.log("üì§ Sending request to generateMCQs with config:", config);

        const rawQuestions = await generateMCQs({
          topics: config.topics,
          questionCount: Number(config.questionCount),
          difficulty: config.difficulty,
          type: config.type,
        });

        console.log("üì• Raw response from generateMCQs:", rawQuestions);

        const parsed = JSON.parse(rawQuestions);
        console.log("‚úÖ Parsed questions array:", parsed);

        if (!Array.isArray(parsed) || parsed.length === 0) {
          throw new Error("Parsed questions are empty or invalid.");
        }

        setQuestions(parsed);
      } catch (err) {
        console.error("‚ùå Failed to load or parse questions:", err);
        alert("Failed to load questions. Try again.");
        navigate("/");
      } finally {
        setLoading(false);
      }
    }

    fetchQuestions();
  }, [config, navigate]);

  // STEP 3: Answer logic
  function setAnswerForCurrent(ans) {
    const updated = {
      ...answers,
      [currentIndex]: ans,
    };
    console.log("üß† Updated answers state:", updated);
    setAnswers(updated);
  }

  function nextQuestion() {
    if (currentIndex < questions.length - 1) setCurrentIndex(currentIndex + 1);
  }

  function prevQuestion() {
    if (currentIndex > 0) setCurrentIndex(currentIndex - 1);
  }

  function submitTest() {
    const resultData = { answers, questions, config };
    localStorage.setItem("mockResult", JSON.stringify(resultData));
    console.log("üì¶ Test submitted. Stored in localStorage:", resultData);
    navigate("/result");
  }

  // STEP 4: Conditional rendering
if (loading) {
  console.log("‚è≥ Exiting early: loading is true");
  return <Loader />;
}

if (timeExpired) {
  console.log("‚è∞ Exiting early: timeExpired is true");
  alert("‚è∞ Time is up! Submitting test.");
  submitTest();
  return null;
}

if (!questions) {
  console.log("‚ùå Exiting early: questions is null");
} else if (questions.length === 0) {
  console.log("‚ùå Exiting early: questions is an empty array");
}

if (!questions || questions.length === 0) {
  return (
    <div className="container py-4">
      <h4>‚ùå Failed to load questions. Please try again.</h4>
      <button className="btn btn-primary mt-3" onClick={() => navigate("/")}>
        üîÑ Go Back to Home
      </button>
    </div>
  );
}


  console.log("üìù Rendering question:", questions[currentIndex]);
  console.log("üë§ User answer so far:", answers[currentIndex] || []);

  // STEP 5: Main render
  return (
    <div className="container py-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <Timer
          durationMinutes={config.duration}
          paused={paused}
          onExpire={() => setTimeExpired(true)}
        />
        <div>
          <button
            className="btn btn-warning me-2"
            onClick={() => setPaused((p) => !p)}
          >
            {paused ? "Resume" : "Pause"}
          </button>
          <button className="btn btn-danger" onClick={submitTest}>
            Submit Test
          </button>
        </div>
      </div>

      <QuestionCard
        question={questions[currentIndex]}
        index={currentIndex}
        total={questions.length}
        userAnswer={answers[currentIndex] || []}
        setUserAnswer={setAnswerForCurrent}
        type={config.type}
      />

      <div className="d-flex justify-content-between mt-3">
        <button
          className="btn btn-secondary"
          onClick={prevQuestion}
          disabled={currentIndex === 0}
        >
          Previous
        </button>
        <button
          className="btn btn-secondary"
          onClick={nextQuestion}
          disabled={currentIndex === questions.length - 1}
        >
          Next
        </button>
      </div>
    </div>
  );
}
