// src/utils/openai.js
export async function generateMCQs({ topics, questionCount, difficulty, type }) {
  const prompt = `
Generate a JSON array of ${questionCount} MCQs about the following topics: ${topics}.
Difficulty level: ${difficulty}.
Question type: ${type === "single" ? "Single correct" : "Multiple correct"} answer.

Each question object should have:
- "questionText" (string)
- "options" (array of strings)
- "correctAnswers" (array of strings)

Respond ONLY with valid JSON.
`;

  const res = await fetch("https://api.openai.com/v1/chat/completions", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      Authorization: `Bearer ${process.env.REACT_APP_OPENAI_API_KEY}`,
    },
    body: JSON.stringify({
      model: "gpt-4o-mini",
      messages: [{ role: "user", content: prompt }],
      temperature: 0.7,
    }),
  });

  if (!res.ok) {
    throw new Error("OpenAI API request failed");
  }

  const data = await res.json();
  return data.choices[0].message.content.trim();
}
